(()=>{"use strict";const e="http://localhost:3000";const n=parseInt(new URLSearchParams(window.location.search).get("id"));(async()=>{await async function(n){const t=await(async n=>(await fetch(`${e}/games/${n}`)).json())(n);document.getElementById("game-name-title").innerHTML=t.name,document.getElementById("game-image").src=t.image,document.getElementById("game-image").alt=t.name,document.getElementById("metacritic-score").innerHTML=t.scores.metacritic,document.getElementById("user-score").innerHTML=t.scores.userScore,document.getElementById("summary").innerHTML=t.summary,document.getElementById("platform").innerHTML=t.platform,document.getElementById("release-date").innerHTML=t.relaseDate}(n),await async function(n){let t=await(async n=>(await fetch(`${e}/games/${n}/comments?_expand=user`)).json())(n);document.getElementById("comments").appendChild(document.createElement("ul")).setAttribute("id","comments-list");for(var a,c=0;c<t.length;c++)document.getElementById("comments-list").appendChild(document.createElement("li")).innerHTML=`<div class="row">\n    <div class="col col-2">\n      <div class="image-container text-right">\n        <img class="avatar rounded" src="${(a=t[c]).user.image}" alt="${a.user.name}" />\n      </div>\n    </div>\n    <div class="col col-10">\n      <div class="user-name-container">\n        <h4 class="text-muted">${a.user.name}</h4>\n      </div>\n      <div class="comment-body-container">\n        ${a.body}\n      </div>\n      <div class="date-container">\n        Comment date: <b>${a.commentDate}</b>\n      </div>\n    </div>\n  </div>`}(n)})()})();