(()=>{"use strict";(async()=>{await async function(){const e=await(async()=>(await fetch("http://localhost:3000/games")).json())(),t=document.createElement("div");t.setAttribute("id","game-list"),t.setAttribute("class","row"),document.getElementById("games").appendChild(t);for(let t=0;t<e.length;t++){const n=document.createElement("div");n.setAttribute("class","col-6 col-sm-3"),document.getElementById("game-list").appendChild(n).innerHTML=`<div class="game-snippet">\n    <a href="/detail.html?id=${(a=e[t]).id}">\n      <div class="game-snippet-container">\n        <img class="img-thumbnail" id="game-${a.id}" src="${a.image}" alt="Cover of game ${a.name}" />\n      </div>\n      <h3 class="game-name">${a.name}</h3>\n    </a>\n  </div>`}var a}()})()})();